import React, { useState } from "react";
import "../style/Cards.css";
import { Link } from "react-router-dom";

const PAGE_PRODUCTS = "products";
const PAGE_CART = "cart";

const Armor = () => {
  const [products] = useState([
    {
      name: "full armor",
      image: "../images/armure1.png",
      cost: "1254",
      etat: "./images/denier.png",
    },
    {
      name: "helmet",
      image: "../images/armure2.png",
      cost: "1620",
      etat: "./images/denier.png",
    },
    {
      name: "full armor",
      image: "../images/armure3.png",
      cost: "899",
      etat: "./images/denier.png",
    },
    {
      name: "full armor",
      image: "../images/armure4.png",
      cost: "1845",
      etat: "./images/denier.png",
    },
    {
      name: "full armor",
      image: "../images/armure5.png",
      cost: "1540",
      etat: "./images/denier.png",
    },
    {
      name: "full armor",
      image: "../images/armure6.png",
      cost: "1950",
      etat: "./images/denier.png",
    },
    {
      name: "full armor",
      image: "../images/armure7.png",
      cost: "1440",
      etat: "./images/denier.png",
    },
    {
      name: "full armor",
      image: "../images/armure8.png",
      cost: "1845",
      etat: "./images/denier.png",
    },
  ]);
  const [cart, setCart] = useState([]);
  const [page, setPage] = useState(PAGE_PRODUCTS);

  const addToCart = (product) => {
    setCart([...cart, { ...product }]);
  };

  const removeFromCart = (productToRemove) => {
    setCart(cart.filter((product) => product !== productToRemove));
  };

  const navigateTo = (nextPage) => {
    setPage(nextPage);
  };

  const renderProducts = () => (
    <>
      {products.map((product, index) => (
        <div className="product" key={index}>
          <h3>{product.name}</h3>
          <img
            style={{ width: "100px", height: "100px" }}
            src={product.image}
            alt={product.name}
          />
          <h4>{product.cost}</h4>
          <img
            style={{ width: "30px", height: "30px" }}
            src={product.etat}
            alt={product.name}
          />
          <button className="btn-card" onClick={() => addToCart(product)}>
            Add to basket
          </button>
        </div>
      ))}
    </>
  );

  const renderCart = () => (
    <>
      <Link to="/order">
        <button className="btn btn-default">Achat</button>
      </Link>
      {cart.map((product, index) => (
        <div className="product" key={index}>
          <h3>{product.name}</h3>
          <img
            style={{ width: "100px", height: "100px" }}
            src={product.image}
            alt={product.name}
          />
          <h4>{product.cost}</h4>
          <img
            style={{ width: "30px", height: "30px" }}
            src={product.etat}
            alt={product.name}
          />
          <button className="btn-card" onClick={() => removeFromCart(product)}>
            Remove
          </button>
        </div>
      ))}
    </>
  );

  return (
    <div>
      <header>
        <div className="container-header">
          <div className="panier">
            <img
              style={{ width: "50px", height: "50px" }}
              className="panier-img"
              src="../images/panier2.png"
              alt="panier"
              onClick={() => navigateTo(PAGE_CART)}
            />
            ({cart.length})
          </div>
        </div>
      </header>

      <div className="products">
        {page === PAGE_PRODUCTS && renderProducts()}
        {page === PAGE_CART && renderCart()}
      </div>
    </div>
  );
};

export default Armor;
